"use strict";

/**
 * Returns the absolute value of a number-like value
 * @param {string|number} v Number-like value
 * @returns {string}
 */
function abs(v) {
    if(isNaN(v)) throw new TypeError("Argument is not a number-like value");
    let s = `${v}`;
    return (s.startsWith("-") || s.startsWith("+")) ? s.slice(1) : s;
};

// Exports
module.exports = abs;