"use strict";

// Imports
const dgroup = require("../utils/dgroup.js");

/**
 * Divides two number-like values
 * @param {string|number} a Number-like value
 * @param {string|number} b Number-like value
 * @param {?string|number} d Decimal place
 * @returns {string}
 */
function divide(a, b, d) {
    return dgroup(a, b, d ?? 16).quotient;
};

// Exports
module.exports = divide;