"use strict";

// Imports
const dgroup = require("../utils/dgroup.js");

/**
 * Finds the modulus of two number-like values
 * @param {string|number} a Number-like value
 * @param {string|number} b Number-like value
 * @param {?string|number} d Decimal place
 * @returns {string}
 */
function modulo(a, b, d) {
    return dgroup(a, b, d ?? 0).modulus;
};

// Exports
module.exports = modulo;