"use strict";

/**
 * Formats and shifts the parsed number-like values
 * @param {[ "+"|"-", string, string ][]} pa Array of parsed number-like values
 * @param {boolean} s Whether or not to include signs
 * @returns {{ array: string[], length: number }}
 */
function format(pa, s) {
    let l = Math.max(...pa.map(pn => pn[2].length));
    let a = pa.map(pn => `${pn.join("")}${"0".repeat(l - pn[2].length)}`);
    if(s === false) a = a.map(e => e.slice(1));
    return { array: a, length: l };
};

// Exports
module.exports = format;