"use strict";

// Imports
const abs = require("../functions/absolute.js");

/**
 * Parses a number-like value into an array
 * @param {string|number} v Number-like value
 * @returns {[ "+"|"-", string, string ]}
 */
function parse(v) {
    if(isNaN(v)) throw new TypeError("Argument is not a number-like value");
    return `${parseFloat(v) >= 0 ? "+" : "-"}.${abs(v)}.`.split(".").slice(0, 3);
};

// Exports
module.exports = parse;