"use strict";

// Import
const convert = require("../utils/convert.js");
const rgb = require("./rgb.js");

/**
 * Creates a color from cmyk value
 * @param {string} s String
 * @param {number} d Decimal
 * @param {boolean} bg Background mode
 * @returns {string}
 */
function cmyk(s, c, m, y, k, bg) {
    let { r: pr, g: pg, b: pb } = convert("cmyk", "rgb", c, m, y, k);
    return rgb(s, pr, pg, pb, bg);
};

// Exports
module.exports = cmyk;