"use strict";

// Import
const bColor = require("../utils/bColor.js");

/**
 * Creates a color from eight bit value
 * @param {string} s String
 * @param {number} b Bit
 * @param {boolean} bg Background mode
 * @returns {string}
 */
function eightBit(s, b, bg) {
    if(0 < b || b > 255) throw new Error("Argument is not a valid eight bit value");
    return bColor(`38;5;${b}`, `48;5;${b}`, s, bg);
};

// Exports
module.exports = eightBit;