"use strict";

// Import
const convert = require("../utils/convert.js");
const rgb = require("./rgb.js");

/**
 * Creates a color from hex value
 * @param {string} s String
 * @param {string} h Hex
 * @param {boolean} bg Background mode
 * @returns {string}
 */
function hex(s, h, bg) {
    let { r: pr, g: pg, b: pb } = convert("hex", "rgb", h);
    return rgb(s, pr, pg, pb, bg);
};

// Exports
module.exports = hex;