"use strict";

// Import
const convert = require("../utils/convert.js");
const rgb = require("./rgb.js");

/**
 * Creates a color from hsl value
 * @param {string} s String
 * @param {string} h Hue
 * @param {string} st Saturation
 * @param {string} l Lightness
 * @param {boolean} bg Background mode
 * @returns {string}
 */
function hsl(s, h, st, l, bg) {
    let { r: pr, g: pg, b: pb } = convert("hsl", "rgb", h, st, l);
    return rgb(s, pr, pg, pb, bg);
};

// Exports
module.exports = hsl;