"use strict";

// Imports
const rgb = require("../advanced/rgb.js");

// Variables
let customs = {
    brown: (s, bg) => rgb(s, 127, 63, 0, bg),

    lime: (s, bg) => rgb(s, 191, 255, 0, bg),
    
    orange: (s, bg) => rgb(s, 255, 127, 0, bg),

    pink: (s, bg) => rgb(s, 255, 191, 191, bg),

    purple: (s, bg) => rgb(s, 127, 0, 255, bg),

    trueBlack: (s, bg) => rgb(s, 0, 0, 0, bg),

    trueBlue: (s, bg) => rgb(s, 0, 0, 255, bg),

    trueCyan: (s, bg) => rgb(s, 0, 255, 255, bg),

    trueGreen: (s, bg) => rgb(s, 0, 255, 0, bg),

    trueGray: (s, bg) => rgb(s, 127, 127, 127, bg),
    get trueGrey() { return customs.trueGray },

    trueMagenta: (s, bg) => rgb(s, 255, 0, 255, bg),

    trueRed: (s, bg) => rgb(s, 255, 0, 0, bg),

    trueYellow: (s, bg) => rgb(s, 255, 255, 0, bg),

    trueWhite: (s, bg) => rgb(s, 255, 255, 255, bg)
};

// Exports
module.exports = customs;