"use strict";

// Imports
const tag = require("../utils/tag.js");

/**
 * Creates a base color from starting and ending tags
 * @param {string} fgt Foreground tag
 * @param {string} bgt Background tag
 * @param {string} s String
 * @param {boolean} bg Background mode
 * @returns 
 */
function bColor(fgt, bgt, s, bg) {
    if([ fgt, bgt, s ].some(v => typeof v !== "string")) throw new TypeError("Argument is not a string");
    if(bg === true) return tag(bgt) + s.replace(/\x1b\[49m/g, tag(bgt)) + tag("49");
    else return tag(fgt) + s.replace(/\x1b\[39m/g, tag(fgt)) + tag("39");
};

// Exports
module.exports = bColor;