"use strict";

// Imports
const tag = require("../utils/tag.js");

/**
 * Creates a base style from starting and ending tags
 * @param {string} sgt Starting tag
 * @param {string} egt Ending tag
 * @param {string} s String
 * @returns 
 */
function bStyle(sgt, egt, s) {
    if([ sgt, egt, s ].some(v => typeof v !== "string")) throw new TypeError("Argument is not a string");
    return (sgt ? tag(sgt) : "") + s + (egt ? tag(egt) : "");
};

// Exports
module.exports = bStyle;